mapper.select:
select * from book_order where 1 = 1 

	and name like '%${name}%' 


	and status = #{status}  


	and accountId = #{accountId}  


	and roomTypeId = #{roomTypeId}  


	and idCard like '%${idCard}%' 


	and mobile like '%${mobile}%' 


	limit #{offset},#{pageSize}

select count(*) from book_order where 1=1  

	and name like '%${name}%' 


	and status = #{status}  


	and accountId = #{accountId}  


	and roomTypeId = #{roomTypeId}  


	and idCard like '%${idCard}%' 


	and mobile like '%${mobile}%'
